import styled from "styled-components"

const StyleMain = styled.div`
  overflow: hidden;

  .section-header {
    height: 100vh;
    background: linear-gradient(160deg, #ffffff 0%,#c5c5c5 100%);
     h1{
         margin: 0;
         padding-top: 200px;
         font-size: 5em;
         font-weight: 200
     }
     p{
         font-weight: 140;
         font-size: 2.3em;
         line-height: .2em;
         margin-boton: 200px;
     }
     img{
         width: 30%;
     }
  }
  .section-footer {
    height: 70vh;
    background: linear-gradient(200deg,#c5c5c5 0%,#ffffff 100%)
  }

  .sticky, .sticky2 {
    height: 100vh;
    background: linear-gradient(160deg, #e4f5fc 0%,#bfe8f9 26%,#9fd8ef 59%,#0068f2 100%);
    width: 100%;

    & .animation, .animation2 {
      width: 100%;
      height: 100%;
      position: absolute;

      & svg {
        opacity: 1;
        position: absolute;
        width: 300px;
        height: 300px;
      }
    }

    .heading {
      position: absolute;
      height: 100%;
      width: 100%;

      h2 {
        font-size: 40px;
        position: absolute;
        bottom: 10%;
        left: 10%;
        margin: 0;
      }
    }

  }
`;

export default StyleMain
